} uint8_t read_data() { Wire.requestFrom(DS3231_ADDRESS, 1); return Wire.read();
} void read_data(uint8_t* data, uint8_t length) { Wire.requestFrom(DS3231_ADDRESS, length); for (uint8_t i = 0; i < length; i++) { data[i] = Wire.read(); }
}

