#define MPU_AFS_ADDR 0x1C
#define MPU_EN_INT 0x38
#define MPU_ACCEL_ADDR 0x3B
#define MPU_GYRO_ADDR 0x43
#define MPU_TEMP_ADDR 0x41
#define MPU_FIFO_EN 0x6A
#define MPU_RESET 0x6B
#define MPU_ID_ADDR 0x75 void write_data(uint8_t addr, uint8_t data);
uint8_t read_data(uint8_t addr);
void read_data(uint8_t addr, uint8_t* data, uint8_t length);
bool MPU_init();
float readTemp();
void readRawAccel(int16_t* accel);
void readRawGyro(int16_t* gyro); void setup() { Serial.begin(115200); if (MPU_init()) { Serial.println("MPU init success!"); } else { Serial.println("MPU init failed!"); }
} void loop() { int16_t accel[3], gyro[3]; readRawAccel(accel); readRawGyro(gyro); Serial.print("Accel X="); Serial.print(accel[0]); Serial.print(",Y="); Serial.print(accel[1]); Serial.print(",Z="); Serial.println(accel[2]); Serial.print("Gyro X="); Serial.print(gyro[0]); Serial.print(",Y="); Serial.print(gyro[1]); Serial.print(",Z="); Serial.println(gyro[2]); Serial.print("Temp="); Serial.println(readTemp(), 2); Serial.println(); delay(1000);
} void write_data(uint8_t addr, uint8_t data) { Wire.beginTransmission(MPU_ADDR); Wire.write(addr); Wire.write(data); Wire.endTransmission();
} uint8_t read_data(uint8_t addr) { Wire.beginTransmission(MPU_ADDR); Wire.write(addr); Wire.endTransmission(); Wire.requestFrom(MPU_ADDR, 1); return Wire.read();
} void read_data(uint8_t addr, uint8_t* dest, uint8_t length) { Wire.beginTransmission(MPU_ADDR); Wire.write(addr); Wire.endTransmission(); Wire.requestFrom(MPU_ADDR, length); for (uint8_t i = 0; i < length; i++) { dest[i] = Wire.read(); }
} bool MPU_init() { // 初始化I2C Wire.begin(); // 设置I2C频率为100KHz Wire.setClock(100 * 1000); // Wake up MPU from sleep mode write_data(MPU_RESET, 0x00); // Disable FIFO write_data(MPU_FIFO_EN, 0x00); // Disable FSYNC write_data(MPU_FSYNC_ADDR, 0x00); // Disable interupt write_data(MPU_EN_INT, 0x00); // Set Gyroscope full scable range to ±250°/s write_data(MPU_GFS_ADDR, 0x00); // Set Accelerometer full scable range to ±2g write_data(MPU_AFS_ADDR, 0x00); // Check ID if (read_data(MPU_ID_ADDR) == 0x71) return true; else return false;
} void readRawAccel(int16_t* accel) { uint8_t buffer[6]; read_data(MPU_ACCEL_ADDR, buffer, 6); for (uint8_t i = 0; i < 3; i++) { accel[i] = buffer[i * 2] << 8 | buffer[i * 2 + 1]; // 第一个字节是高位，第二字节是低位 }
} void readRawGyro(int16_t* gyro) { uint8_t buffer[6]; read_data(MPU_GYRO_ADDR, buffer, 6); for (uint8_t i = 0; i < 3; i++) { gyro[i] = buffer[i * 2] << 8 | buffer[i * 2 + 1]; // 第一个字节是高位，第二字节是低位 }
} float readTemp() { int16_t temp; uint8_t buffer[2]; read_data(MPU_TEMP_ADDR, buffer, 2); temp = buffer[0] << 8 | buffer[1]; // 第一个字节是高位，第二字节是低位 return (temp / 321.0 + 21); // 温度计算公式：TEMP_degC = TEMP_OUT/321.0 +21
}

